import React from 'react';

interface ButtonProps {
  variant: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant, onClick, disabled = false, children }) => {
  const baseStyle = 'px-4 py-2 rounded transition duration-200';
  const variantStyles = {
    primary: 'bg-blue-500 text-white hover:bg-blue-600 active:bg-blue-700',
    secondary: 'bg-gray-500 text-white hover:bg-gray-600 active:bg-gray-700',
  };

  const disabledStyle = disabled ? 'opacity-50 cursor-not-allowed' : '';

  return (
    <button
      className={`${baseStyle} ${variantStyles[variant]} ${disabledStyle}`}
      onClick={onClick}
      disabled={disabled}
      aria-label={children.toString()}
    >
      {children}
    </button>
  );
};

export default Button;
