import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, disabled = false, children }) => {
  const baseStyles = 'px-4 py-2 rounded';
  const variantStyles = variant === 'primary'
    ? 'bg-blue-600 text-white hover:bg-blue-700 active:bg-blue-800'
    : 'bg-gray-600 text-white hover:bg-gray-700 active:bg-gray-800';
  const disabledStyles = disabled ? 'bg-gray-300 text-gray-600 cursor-not-allowed' : '';

  return (
    <button
      onClick={onClick}
      className={`${baseStyles} ${variantStyles} ${disabledStyles}`}
      disabled={disabled}
      aria-pressed={variant}
    >
      {children}
    </button>
  );
};

export default Button;
