import React, { useState } from 'react';

/**
 * CounterDisplay component displays the current count.
 * @component
 */
const CounterDisplay: React.FC = () => {
  const [count, setCount] = useState<number>(0);

  const increment = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrement = () => {
    setCount(prevCount => (prevCount > 0 ? prevCount - 1 : 0));
  };

  const reset = () => {
    setCount(0);
  };

  return (
    <div className="flex flex-col items-center">
      <h1 className="text-2xl font-bold text-center">{count}</h1>
      <div className="flex gap-2 mt-4">
        <button className="bg-blue-500 text-white py-2 px-4 rounded" onClick={increment}>INCREMENT</button>
        <button className="bg-red-500 text-white py-2 px-4 rounded" onClick={decrement}>DECREMENT</button>
        <button className="bg-gray-500 text-white py-2 px-4 rounded" onClick={reset}>RESET</button>
      </div>
    </div>
  );
};

export default CounterDisplay;
