import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, disabled = false, children }) => {
  const baseStyles = 'px-4 py-2 rounded transition duration-200';
  const variants = {
    primary: {
      base: 'bg-blue-600 text-white border-none',
      hover: 'bg-blue-500',
      active: 'bg-blue-700',
      disabled: 'bg-gray-300 text-gray-500',
    },
    secondary: {
      base: 'bg-gray-600 text-white border-none',
      hover: 'bg-gray-500',
      active: 'bg-gray-700',
      disabled: 'bg-gray-300 text-gray-500',
    },
  };

  const { base, hover, active, disabled: disabledStyles } = variants[variant];

  return (
    <button
      className={`${baseStyles} ${disabled ? disabledStyles : base} hover:${hover} active:${active}`}
      onClick={disabled ? undefined : onClick}
      disabled={disabled}
      aria-disabled={disabled}
    >
      {children}
    </button>
  );
};

export default Button;
