import React, { useState } from 'react';

interface CardProps {
  title: string;
  description: string;
  variant?: 'default' | 'highlighted';
  onClick?: () => void;
}

const Card: React.FC<CardProps> = ({ title, description, variant = 'default', onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => setIsHovered(true);
  const handleMouseLeave = () => setIsHovered(false);

  return (
    <div
      className={`bg-white shadow-md rounded-md p-4 transition-transform ${variant === 'highlighted' ? 'border-2 border-blue-500' : ''} ${isHovered ? 'scale-105' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyPress={(e) => e.key === 'Enter' && onClick && onClick()}
      aria-label={title}
    >
      <h2 className="text-lg font-semibold">{title}</h2>
      <p className="text-gray-600">{description}</p>
    </div>
  );
};

export default Card;
