import React from 'react';
import PropTypes from 'prop-types';

interface ButtonProps {
  variant: 'primary' | 'secondary';
  state?: 'default' | 'hover' | 'active' | 'disabled';
  onClick: () => void;
  children: React.ReactNode;
  icon?: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant, state = 'default', onClick, children, icon }) => {
  const baseStyles = 'flex items-center gap-2 px-4 py-2 rounded transition duration-200';
  const variantStyles = variant === 'primary'
    ? 'bg-blue-500 text-white'
    : 'bg-gray-500 text-white';
  const stateStyles = state === 'disabled'
    ? 'opacity-50 cursor-not-allowed'
    : 'hover:bg-opacity-80 active:bg-opacity-90 cursor-pointer';

  const handleClick = () => {
    if (state !== 'disabled') {
      onClick();
    }
  };

  return (
    <button
      className={`${baseStyles} ${variantStyles} ${stateStyles}`}
      onClick={handleClick}
      disabled={state === 'disabled'}
      aria-disabled={state === 'disabled'}
    >
      {icon && <span>{icon}</span>}
      {children}
    </button>
  );
};

Button.propTypes = {
  variant: PropTypes.oneOf(['primary', 'secondary']).isRequired,
  state: PropTypes.oneOf(['default', 'hover', 'active', 'disabled']),
  onClick: PropTypes.func.isRequired,
  children: PropTypes.node.isRequired,
  icon: PropTypes.node,
};

export default Button;
