import React, { useState } from 'react';

/**
 * CounterDisplay component to display and manage a counter.
 * @returns {JSX.Element}
 */
const CounterDisplay: React.FC = () => {
  const [count, setCount] = useState<number>(0);

  const increment = () => {
    setCount(prevCount => prevCount + 1);
  };

  const decrement = () => {
    setCount(prevCount => (prevCount > 0 ? prevCount - 1 : 0));
  };

  const clear = () => {
    setCount(0);
  };

  return (
    <div className="flex flex-col items-center">
      <h1 className="text-2xl font-bold m-0">{count}</h1>
      <div className="flex gap-2">
        <button onClick={increment} className="bg-blue-500 text-white px-4 py-2 rounded">+</button>
        <button onClick={decrement} className="bg-red-500 text-white px-4 py-2 rounded">-</button>
        <button onClick={clear} className="bg-gray-500 text-white px-4 py-2 rounded">Clear</button>
      </div>
    </div>
  );
};

export default CounterDisplay;
