import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { fetchBuildData } from '../api/builds';

interface UnderstandingPanelProps {
  variant?: 'default' | 'detailed';
}

const UnderstandingPanel: React.FC<UnderstandingPanelProps> = ({ variant = 'default' }) => {
  const [data, setData] = useState<{ components: string[]; decisions: string[]; risks: string[] } | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        const buildData = await fetchBuildData();
        setData(buildData);
      } catch (err) {
        setError('Failed to load data.');
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, []);

  if (loading) {
    return <div className='text-center'>Loading...</div>;
  }

  if (error) {
    return <div className='text-red-500'>{error}</div>;
  }

  return (
    <div className={`p-4 bg-slate-800 text-white rounded-lg`}>  
      <h2 className='text-xl font-bold'>Understanding Your Build</h2>
      <div className='mt-4'>
        <h3 className='text-lg font-semibold'>What was built:</h3>
        <ul className='list-disc pl-5'>
          {data.components.map((component, index) => (
            <li key={index}>{component}</li>
          ))}
        </ul>
      </div>
      <div className='mt-4'>
        <h3 className='text-lg font-semibold'>Why it was built this way:</h3>
        <ul className='list-disc pl-5'>
          {data.decisions.map((decision, index) => (
            <li key={index}>{decision}</li>
          ))}
        </ul>
      </div>
      <div className='mt-4'>
        <h3 className='text-lg font-semibold'>Known risks:</h3>
        <ul className='list-disc pl-5'>
          {data.risks.map((risk, index) => (
            <li key={index}>{risk}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

UnderstandingPanel.propTypes = {
  variant: PropTypes.oneOf(['default', 'detailed']),
};

export default UnderstandingPanel;
