import React from 'react';
import PropTypes from 'prop-types';

/**
 * Card component to display content in a styled container.
 *
 * @param {React.PropsWithChildren<CardProps>} props - The props for the Card component.
 * @param {string} [props.className] - Additional class names for custom styling.
 * @param {React.ReactNode} props.children - The content to display inside the card.
 * @returns {JSX.Element} The rendered Card component.
 */
const Card = ({ className, children }: React.PropsWithChildren<{ className?: string }>): JSX.Element => {
  return (
    <div className={`bg-white border border-gray-300 rounded p-4 ${className}`.trim()}>
      {children}
    </div>
  );
};

Card.propTypes = {
  className: PropTypes.string,
  children: PropTypes.node.isRequired,
};

export default Card;
