import React, { useState } from 'react';

interface InputProps {
  variant?: 'text' | 'email' | 'password';
  value: string;
  onChange: (value: string) => void;
  error?: string;
}

const Input: React.FC<InputProps> = ({ variant = 'text', value, onChange, error }) => {
  const [isFocused, setIsFocused] = useState(false);
  const baseStyles = 'border-1 rounded py-2 px-3 transition';
  const errorStyles = error ? 'border-red-500' : 'border-gray-300';
  const focusStyles = isFocused ? 'border-blue-500' : '';

  return (
    <div>
      <input
        type={variant}
        value={value}
        onChange={(e) => onChange(e.target.value)}
        onFocus={() => setIsFocused(true)}
        onBlur={() => setIsFocused(false)}
        className={`${baseStyles} ${errorStyles} ${focusStyles}`}
        aria-invalid={!!error}
      />
      {error && <span className='text-red-500'>{error}</span>}
    </div>
  );
};

export default Input;
