import React from 'react';
import { useState } from 'react';
import styles from './Hero.module.css';

interface HeroProps {
  headline: string;
  subheadline: string;
  ctaText: string;
  ctaOnClick: () => void;
  variant?: 'default' | 'featured';
}

const Hero: React.FC<HeroProps> = ({
  headline,
  subheadline,
  ctaText,
  ctaOnClick,
  variant = 'default',
}) => {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  return (
    <div
      className={`flex flex-col items-center justify-center h-screen ${variant === 'featured' ? 'bg-gray-100' : ''}`}
    >
      <h1 className={`text-3xl font-bold ${variant === 'featured' ? 'text-blue-600' : 'text-gray-900'}`}
      >
        {headline}
      </h1>
      <p className={`text-lg ${variant === 'featured' ? 'text-blue-600' : 'text-gray-600'}`}
      >
        {subheadline}
      </p>
      <button
        className={`bg-orange-500 hover:bg-orange-700 text-white font-bold py-2 px-4 rounded ${variant === 'featured' ? 'mt-4' : 'mt-2'}`}
        onClick={ctaOnClick}
        onFocus={handleFocus}
        onBlur={handleBlur}
      >
        {ctaText}
      </button>
    </div>
  );
};

export default Hero;
