import React, { useState } from 'react';

interface InputProps {
  type: 'text' | 'password';
  placeholder?: string;
  onChange: (value: string) => void;
  error?: boolean;
}

const Input: React.FC<InputProps> = ({ type, placeholder, onChange, error = false }) => {
  const [isFocused, setIsFocused] = useState(false);

  const baseStyle = 'border rounded p-2 transition duration-200';
  const errorStyle = error ? 'border-red-500' : 'border-gray-300';
  const focusStyle = isFocused ? 'border-blue-500' : '';

  return (
    <input
      type={type}
      placeholder={placeholder}
      className={`${baseStyle} ${errorStyle} ${focusStyle}`}
      onFocus={() => setIsFocused(true)}
      onBlur={() => setIsFocused(false)}
      onChange={(e) => onChange(e.target.value)}
      aria-invalid={error}
    />
  );
};

export default Input;
