// components/Modal.tsx

import * as React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { cn } from '@/lib/utils';

interface ModalProps {
  size?: 'sm' | 'md' | 'lg' | 'full';
  title?: string;
  onClose: () => void;
  isOpen: boolean;
  children: React.ReactNode;
}

const modalVariants = {
  hidden: { opacity: 0 },
  visible: { opacity: 1 },
};

const Modal: React.FC<ModalProps> = ({ size = 'md', title, onClose, isOpen, children }) => {
  return (
    <AnimatePresence>
      {isOpen && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-overlay">
          <motion.div
            className={cn('bg-background rounded-lg shadow-lg p-6', modalVariants[size])}
            variants={modalVariants}
            initial="hidden"
            animate="visible"
            exit="hidden"
            transition={{ duration: 0.2 }}
            role="dialog"
            aria-labelledby="modal-title"
            aria-describedby="modal-description"
            aria-hidden={!isOpen}
          >
            <div className="flex justify-between items-center">
              {title && <h2 id="modal-title" className="text-lg font-semibold">{title}</h2>}
              <button className="text-muted-foreground" onClick={onClose}>âœ•</button>
            </div>
            <div id="modal-description" className="mt-4">
              {children}
            </div>
          </motion.div>
        </div>
      )}
    </AnimatePresence>
  );
};

export default Modal;
