import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, disabled = false, children }) => {
  const baseStyles = 'px-4 py-2 rounded focus:outline-none';
  const primaryStyles = 'bg-blue-600 text-white hover:bg-blue-700 active:bg-blue-800';
  const secondaryStyles = 'bg-gray-600 text-white hover:bg-gray-700 active:bg-gray-800';
  const disabledStyles = 'bg-gray-400 text-gray-600 cursor-not-allowed';

  const styles = variant === 'primary' ? primaryStyles : secondaryStyles;
  return (
    <button
      className={`${baseStyles} ${disabled ? disabledStyles : styles}`}
      onClick={disabled ? undefined : onClick}
      disabled={disabled}
      aria-disabled={disabled}
    >
      {children}
    </button>
  );
};

export default Button;
