import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary' | 'danger';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, disabled = false, children }) => {
  const baseStyles = 'min-w-[80px] px-4 py-2 rounded';
  const variantStyles = {
    primary: 'bg-blue-500 text-white',
    secondary: 'bg-gray-300 text-black',
    danger: 'bg-red-500 text-white',
  }[variant];
  const disabledStyles = disabled ? 'opacity-50 cursor-not-allowed' : 'hover:opacity-75';

  return (
    <button
      className={`${baseStyles} ${variantStyles} ${disabledStyles}`}
      onClick={onClick}
      disabled={disabled}
      aria-disabled={disabled}
    >
      {children}
    </button>
  );
};

export default Button;
