import React, { useState } from 'react';

/**
 * CounterDisplay component that displays and manipulates a counter value.
 *
 * @returns JSX.Element
 */
const CounterDisplay: React.FC = () => {
  const [count, setCount] = useState<number>(0);

  /**
   * Increments the counter by 1.
   */
  const handleIncrement = () => {
    setCount(prevCount => prevCount + 1);
  };

  /**
   * Decrements the counter by 1 if it is greater than 0.
   */
  const handleDecrement = () => {
    setCount(prevCount => (prevCount > 0 ? prevCount - 1 : 0));
  };

  /**
   * Resets the counter to 0.
   */
  const handleClear = () => {
    setCount(0);
  };

  return (
    <div className="flex flex-col items-center">
      <h1 className="text-2xl text-center my-4">Counter: {count}</h1>
      <div className="flex gap-2">
        <button className="px-4 py-2 bg-blue-500 text-white rounded" onClick={handleIncrement}>+</button>
        <button className="px-4 py-2 bg-red-500 text-white rounded" onClick={handleDecrement}>-</button>
        <button className="px-4 py-2 bg-gray-500 text-white rounded" onClick={handleClear}>Clear</button>
      </div>
    </div>
  );
};

export default CounterDisplay;
