import React from 'react';

// ButtonProps defines the properties for the Button component
interface ButtonProps {
  variant?: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, disabled = false, children }) => {
  // Define base styles
  const baseStyles = 'px-4 py-2 rounded focus:outline-none';
  // Define variant styles
  const variantStyles = {
    primary: 'bg-blue-600 text-white hover:bg-blue-700 active:bg-blue-800',
    secondary: 'bg-gray-600 text-white hover:bg-gray-700 active:bg-gray-800',
  };

  // Combine styles based on the variant and disabled state
  const buttonStyles = `${baseStyles} ${variantStyles[variant]} ${disabled ? 'bg-gray-300 cursor-not-allowed' : ''}`;

  return (
    <button className={buttonStyles} onClick={onClick} disabled={disabled} aria-disabled={disabled}>
      {children}
    </button>
  );
};

export default Button;
