import React, { useState } from 'react';

interface InputProps {
  type?: string;
  placeholder?: string;
  value: string;
  onChange: (value: string) => void;
  error?: boolean;
}

const Input: React.FC<InputProps> = ({ type = 'text', placeholder, value, onChange, error = false }) => {
  const [isFocused, setIsFocused] = useState(false);
  const baseStyles = 'border rounded px-4 py-2';
  const focusStyles = isFocused ? 'border-blue-500 shadow-outline' : '';
  const errorStyles = error ? 'border-red-500' : '';

  return (
    <input
      type={type}
      placeholder={placeholder}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      onFocus={() => setIsFocused(true)}
      onBlur={() => setIsFocused(false)}
      className={`${baseStyles} ${focusStyles} ${errorStyles}`}
      aria-invalid={error}
    />
  );
};

export default Input;
