import React from 'react';

interface InputProps {
  variant?: 'text' | 'textarea';
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  placeholder?: string;
  required?: boolean;
}

const Input: React.FC<InputProps> = ({ variant = 'text', value, onChange, placeholder, required = false }) => {
  const baseStyles = 'border border-gray-300 rounded p-2 focus:outline-none focus:ring';
  return (
    variant === 'textarea' ? (
      <textarea
        className={baseStyles}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
      />
    ) : (
      <input
        type="text"
        className={baseStyles}
        value={value}
        onChange={onChange}
        placeholder={placeholder}
        required={required}
      />
    )
  );
};

export default Input;
