import React from 'react';

interface ButtonProps {
  variant: 'primary' | 'secondary';
  onClick: () => void;
  disabled?: boolean;
  children: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant, onClick, disabled = false, children }) => {
  const baseStyles = 'px-4 py-2 rounded focus:outline-none';
  const variants = {
    primary: {
      default: 'bg-blue-600 text-white border-none',
      hover: 'hover:bg-blue-700',
      active: 'active:bg-blue-800',
      disabled: 'bg-gray-400 cursor-not-allowed'
    },
    secondary: {
      default: 'bg-gray-600 text-white border-none',
      hover: 'hover:bg-gray-700',
      active: 'active:bg-gray-800',
      disabled: 'bg-gray-400 cursor-not-allowed'
    }
  };

  const getStyles = () => {
    const variantStyles = variants[variant];
    return `${baseStyles} ${variantStyles.default} ${disabled ? variantStyles.disabled : ''}`;
  };

  return (
    <button
      className={getStyles()}
      onClick={disabled ? undefined : onClick}
      disabled={disabled}
    >
      {children}
    </button>
  );
};

export default Button;
