import React from 'react';

interface InputProps {
  type?: 'email' | 'password' | 'text';
  placeholder?: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  value: string;
  error?: string;
}

const Input: React.FC<InputProps> = ({ type = 'text', placeholder, onChange, value, error }) => {
  const baseStyles = 'px-3 py-2 border rounded';
  const errorStyles = error ? 'border-red-500' : 'border-gray-300';

  return (
    <div>
      <input
        type={type}
        placeholder={placeholder}
        onChange={onChange}
        value={value}
        className={`${baseStyles} ${errorStyles} focus:border-blue-500 hover:border-blue-300`}
        aria-invalid={!!error}
      />
      {error && <p className="text-red-500 text-xs mt-1">{error}</p>}
    </div>
  );
};

export default Input;
