import React, { useState } from 'react';
import PropTypes from 'prop-types';

/**
 * Card component
 *
 * @param {string} title - The title of the card.
 * @param {React.ReactNode} children - The content of the card.
 * @param {React.ReactNode} actions - Actions to display at the bottom of the card.
 */
const Card = ({ title, children, actions }) => {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={`border rounded-lg p-4 transition-shadow ${isHovered ? 'shadow-lg' : 'shadow-sm'}`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
      role="region"  
      aria-labelledby="card-title"
    >
      <h2 id="card-title" className="text-lg font-bold mb-2">{title}</h2>
      <div className="mb-4">{children}</div>
      <div className="flex justify-end gap-2">{actions}</div>
    </div>
  );
};

Card.propTypes = {
  title: PropTypes.string.isRequired,
  children: PropTypes.node.isRequired,
  actions: PropTypes.node,
};

Card.defaultProps = {
  actions: null,
};

export default Card;
