import React from 'react';

interface CardProps {
  title: string;
  children: React.ReactNode;
  actions?: React.ReactNode;
}

/**
 * Card component to display content with a header, body, and actions.
 *
 * @param {CardProps} props - Properties for the card component.
 */
const Card: React.FC<CardProps> = ({ title, children, actions }) => {
  return (
    <div className="bg-white shadow rounded-lg overflow-hidden">
      <div className="p-4 font-bold border-b border-gray-200">
        {title}
      </div>
      <div className="p-4">
        {children}
      </div>
      {actions && (
        <div className="p-4 border-t border-gray-200">
          {actions}
        </div>
      )}
    </div>
  );
};

export default Card;
