import React, { useState } from 'react';

interface InputProps {
  label: string;
  value: string;
  onChange: (value: string) => void;
  type?: 'text' | 'textarea';
  errorMessage?: string;
}

const Input: React.FC<InputProps> = ({ label, value, onChange, type = 'text', errorMessage }) => {
  const [focused, setFocused] = useState(false);

  const handleFocus = () => setFocused(true);
  const handleBlur = () => setFocused(false);

  const inputClasses = `border rounded p-2 ${errorMessage ? 'border-red-500' : 'border-gray-300'} ${focused ? 'focus:ring focus:ring-blue-300' : ''}`;

  return (
    <div>
      <label className="block mb-1">{label}</label>
      {type === 'textarea' ? (
        <textarea
          className={inputClasses}
          value={value}
          onChange={(e) => onChange(e.target.value)}
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
      ) : (
        <input
          type="text"
          className={inputClasses}
          value={value}
          onChange={(e) => onChange(e.target.value)}
          onFocus={handleFocus}
          onBlur={handleBlur}
        />
      )}
      {errorMessage && <p className="text-red-500 text-sm mt-1">{errorMessage}</p>}
    </div>
  );
};

export default Input;
