import React, { useState } from 'react';
import PropTypes from 'prop-types';

/**
 * Card component
 * @param {Object} props
 * @param {string} props.title - Title of the card
 * @param {string} props.content - Content of the card
 * @param {'default' | 'highlighted'} [props.variant='default'] - Variant of the card
 * @param {boolean} [props.isActive=false] - State indicating if the card is active
 * @returns {JSX.Element}
 */
const Card = ({ title, content, variant = 'default', isActive = false }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => setIsHovered(true);
  const handleMouseLeave = () => setIsHovered(false);

  const cardStyles = `
    p-4 
    border 
    rounded-md 
    ${variant === 'highlighted' ? 'border-blue-500 bg-blue-100' : 'border-gray-300 bg-white'}
    ${isActive ? 'shadow-lg' : 'shadow-md'}
    ${isHovered ? 'transform scale-105' : ''}
  `;

  return (
    <div 
      className={cardStyles}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      role="button"
      tabIndex={0}
      aria-label={title}
    >
      <h3 className="text-lg font-semibold">{title}</h3>
      <p>{content}</p>
    </div>
  );
};

Card.propTypes = {
  title: PropTypes.string.isRequired,
  content: PropTypes.string.isRequired,
  variant: PropTypes.oneOf(['default', 'highlighted']),
  isActive: PropTypes.bool,
};

export default Card;
