import React from 'react';

interface DecrementButtonProps {
  onDecrement: () => void;
  disabled: boolean;
}

const DecrementButton: React.FC<DecrementButtonProps> = ({ onDecrement, disabled }) => {
  return (
    <button
      className={`bg-green-500 text-white py-2 px-4 rounded cursor-pointer hover:bg-green-600 ${disabled ? 'bg-gray-300 text-gray-500 cursor-not-allowed' : ''}`}
      onClick={disabled ? undefined : onDecrement}
      disabled={disabled}
      aria-label="Decrement"
    >
      DECREMENT
    </button>
  );
};

export default DecrementButton;
