import React from 'react';

interface InputProps {
  label: string;
  type?: 'text' | 'textarea';
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
  error?: string;
  className?: string;
}

const Input: React.FC<InputProps> = ({ label, type = 'text', value, onChange, error, className }) => {
  return (
    <div className={`flex flex-col ${className}`}>  
      <label className='mb-2'>{label}</label>
      {type === 'textarea' ? (
        <textarea
          value={value}
          onChange={onChange}
          className={`border ${error ? 'border-red-500' : 'border-gray-300'} p-2 rounded`}  
        />
      ) : (
        <input
          type={type}
          value={value}
          onChange={onChange}
          className={`border ${error ? 'border-red-500' : 'border-gray-300'} p-2 rounded`}  
        />
      )}
      {error && <span className='text-red-500 mt-1'>{error}</span>}
    </div>
  );
};

export default Input;
