import React from 'react';

interface ButtonProps {
  variant: 'primary' | 'secondary' | 'tertiary';
  state?: 'default' | 'hover' | 'active' | 'disabled';
  onClick: () => void;
  children: React.ReactNode;
  icon?: React.ReactNode;
  className?: string;
}

const Button: React.FC<ButtonProps> = ({ variant, state = 'default', onClick, children, icon, className }) => {
  const baseStyles = 'px-4 py-2 rounded transition duration-200';
  const variantStyles = {
    primary: 'bg-blue-500 text-white',
    secondary: 'bg-gray-300 text-black',
    tertiary: 'bg-transparent text-blue-500 border border-blue-500',
  }[variant];
  const stateStyles = {
    default: '',
    hover: 'hover:opacity-80',
    active: 'active:opacity-60',
    disabled: 'opacity-50 cursor-not-allowed',
  }[state];

  return (
    <button
      onClick={onClick}
      className={`${baseStyles} ${variantStyles} ${stateStyles} ${className}`}
      disabled={state === 'disabled'}
    >
      {icon && <span className='mr-2'>{icon}</span>}
      {children}
    </button>
  );
};

export default Button;
