// Input.tsx
import React, { useState } from 'react';

interface InputProps {
  variant: 'Text' | 'Search';
  placeholder: string;
  value: string;
  onChange: (value: string) => void;
}

const Input: React.FC<InputProps> = ({ variant, placeholder, value, onChange }) => {
  const [isFocused, setIsFocused] = useState(false);

  const handleFocus = () => {
    setIsFocused(true);
  };

  const handleBlur = () => {
    setIsFocused(false);
  };

  const styles = {
    Text: {
      padding: '8px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
    Search: {
      padding: '8px',
      border: '1px solid #ccc',
      borderRadius: '4px',
    },
  };

  return (
    <input
      type="text"
      className="input"
      style={styles[variant]}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      placeholder={placeholder}
      onFocus={handleFocus}
      onBlur={handleBlur}
      className={isFocused ? 'input-focused' : ''}
    />
  );
};

export default Input;
