// src/components/Toast.tsx

import * as React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { cn } from '@/lib/utils';
import { X } from 'lucide-react';

interface ToastProps {
  variant: 'success' | 'error' | 'info';
  message: string;
  onDismiss: () => void;
  isVisible: boolean;
  duration?: number;
}

export const Toast: React.FC<ToastProps> = ({ variant, message, onDismiss, isVisible, duration = 3000 }) => {
  const [isVisibleState, setIsVisibleState] = React.useState(isVisible);

  React.useEffect(() => {
    if (isVisible) {
      setIsVisibleState(true);
      const timer = setTimeout(() => {
        setIsVisibleState(false);
        onDismiss();
      }, duration);
      return () => clearTimeout(timer);
    }
  }, [isVisible, onDismiss, duration]);

  const handleDismiss = () => {
    setIsVisibleState(false);
    onDismiss();
  };

  return (
    <AnimatePresence>
      {isVisibleState && (
        <motion.div
          className={cn(
            'fixed bottom-4 right-4 rounded-lg shadow-lg transition-opacity duration-300',
            {
              'bg-success text-white': variant === 'success',
              'bg-error text-white': variant === 'error',
              'bg-info text-white': variant === 'info',
            }
          )}
          role="alert"
          aria-live="assertive"
          aria-atomic={true}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
        >
          <div className="flex items-center justify-between p-3">
            <span>{message}</span>
            <button onClick={handleDismiss} aria-label="Dismiss" className="ml-2"> 
              <X className="h-4 w-4" />
            </button>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default Toast;
