import React, { useState } from 'react';
import PropTypes from 'prop-types';

interface CardProps {
  title: string;
  content: string;
  variant?: 'default' | 'highlighted';
  onClick?: () => void;
}

const Card: React.FC<CardProps> = ({ title, content, variant = 'default', onClick }) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => setIsHovered(true);
  const handleMouseLeave = () => setIsHovered(false);

  return (
    <div
      className={`p-4 rounded-lg shadow-lg transition-all duration-300 ${
        variant === 'highlighted' ? 'bg-yellow-300' : 'bg-white'
      } ${isHovered ? 'transform scale-105' : ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyPress={(e) => e.key === 'Enter' && onClick && onClick()}
      aria-label={title}
    >
      <h2 className="text-lg font-semibold mb-2">{title}</h2>
      <p>{content}</p>
    </div>
  );
};

Card.propTypes = {
  title: PropTypes.string.isRequired,
  content: PropTypes.string.isRequired,
  variant: PropTypes.oneOf(['default', 'highlighted']),
  onClick: PropTypes.func,
};

export default Card;
