import React from 'react';

interface ButtonProps {
  variant: 'primary' | 'secondary';
  onClick: () => void;
  children: React.ReactNode;
  icon?: React.ReactNode;
}

const Button: React.FC<ButtonProps> = ({ variant, onClick, children, icon }) => {
  const baseStyles = 'flex items-center justify-center px-4 py-2 min-w-[80px] rounded transition duration-200';
  const variantStyles = variant === 'primary'
    ? 'bg-blue-600 text-white hover:bg-blue-700 active:bg-blue-800'
    : 'bg-gray-600 text-white hover:bg-gray-700 active:bg-gray-800';

  return (
    <button className={`${baseStyles} ${variantStyles}`} onClick={onClick} aria-label={children.toString()}>
      {icon && <span className='mr-2'>{icon}</span>}
      {children}
    </button>
  );
};

export default Button;
