import React, { useState } from 'react';

interface InputProps {
  variant?: 'text' | 'email';
  placeholder?: string;
  onChange: (value: string) => void;
  error?: string;
}

const Input: React.FC<InputProps> = ({ variant = 'text', placeholder = '', onChange, error }) => {
  const [isFocused, setIsFocused] = useState(false);
  const baseStyles = 'border rounded p-2 transition duration-200';
  const focusStyles = isFocused ? 'border-green-500' : 'border-gray-300';
  const errorStyles = error ? 'border-red-500' : '';

  return (
    <div>
      <input
        type={variant}
        placeholder={placeholder}
        className={`${baseStyles} ${focusStyles} ${errorStyles}`}
        onFocus={() => setIsFocused(true)}
        onBlur={() => setIsFocused(false)}
        onChange={(e) => onChange(e.target.value)}
        aria-invalid={!!error}
        aria-describedby={error ? 'error-message' : undefined}
      />
      {error && <p id='error-message' className='text-red-500'>{error}</p>}
    </div>
  );
};

export default Input;
