// Avatar.tsx
import * as React from 'react';

export interface AvatarProps {
  src?: string;
  alt?: string;
  fallback?: React.ReactNode;
  initials?: string;
  size?: 'sm' | 'md' | 'lg';
}

const Avatar: React.FC<AvatarProps> = ({ src, alt, fallback, initials, size = 'md' }) => {
  const sizeClasses = {
    sm: 'h-8 w-8',
    md: 'h-10 w-10',
    lg: 'h-12 w-12',
  }[size];

  return (
    <div className={`relative rounded-full overflow-hidden ${sizeClasses}`}>  
      {src ? <img src={src} alt={alt} className='object-cover' /> : <div className='flex items-center justify-center bg-gray-200'>{initials}</div>}
    </div>
  );
};

export { Avatar };
