import React, { TextareaHTMLAttributes } from 'react';

interface TextareaProps extends TextareaHTMLAttributes<HTMLTextAreaElement> {
  placeholder: string;
}

const Textarea: React.FC<TextareaProps> = ({ placeholder, ...props }) => {
  return (
    <textarea
      className="w-full p-4 bg-[#0d0d0d] border border-white/10 focus:ring-2 focus:ring-primary rounded-md resize-none"
      placeholder={placeholder}
      {...props}
    />
  );
};

export default Textarea;
