import React from 'react';
import PropTypes from 'prop-types';

/**
 * A basic card component
 *
 * @param {Object} props - React props
 * @param {string} props.title - Title of the card
 * @param {string} props.description - Description of the card
 * @param {React.ReactNode} props.children - Child elements to render inside the card
 * @param {boolean} [props.highlighted=false] - Whether the card is highlighted
 * @param {boolean} [props.selected=false] - Whether the card is selected
 * @returns {JSX.Element} The Card component
 */
const Card = ({ title, description, children, highlighted = false, selected = false }) => {
  return (
    <div
      className={`border rounded-lg p-4 transition-transform duration-200 ${highlighted ? 'bg-yellow-100' : 'bg-white'} ${selected ? 'shadow-lg' : 'shadow-md'} hover:shadow-lg`}
      role="button"
      tabIndex={0}
    >
      <h2 className="text-xl font-semibold mb-2">{title}</h2>
      <p className="text-gray-700 mb-4">{description}</p>
      <div>{children}</div>
    </div>
  );
};

Card.propTypes = {
  title: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  children: PropTypes.node,
  highlighted: PropTypes.bool,
  selected: PropTypes.bool,
};

export default Card;
