import React from 'react';
import PropTypes from 'prop-types';

interface CardProps {
  variant: 'Product' | 'Promotion';
  title: string;
  content: string;
  onClick?: () => void;
  state?: 'Hover' | 'Active';
}

const Card: React.FC<CardProps> = ({ variant, title, content, onClick, state }) => {
  const padding = variant === 'Promotion' ? '24px' : '16px';
  const boxShadow = state === 'Active' ? '0 0 10px rgba(0,0,0,0.4)' :
                   state === 'Hover' ? '0 0 10px rgba(0,0,0,0.2)' : 'none';

  return (
    <div
      className={`bg-white rounded-lg shadow transition-all duration-300 p-[${padding}]`} 
      style={{ boxShadow }}
      onClick={onClick}
      role="button"
      tabIndex={0}
      onKeyPress={(e) => e.key === 'Enter' && onClick && onClick()}
    >
      <h2 className="font-bold text-lg mb-2">{title}</h2>
      <p className="text-gray-700">{content}</p>
    </div>
  );
};

Card.propTypes = {
  variant: PropTypes.oneOf(['Product', 'Promotion']).isRequired,
  title: PropTypes.string.isRequired,
  content: PropTypes.string.isRequired,
  onClick: PropTypes.func,
  state: PropTypes.oneOf(['Hover', 'Active']),
};

export default Card;
