import React from 'react';

interface ButtonProps {
  variant?: 'primary' | 'secondary';
  onClick: () => void;
  children: React.ReactNode;
  className?: string;
}

const Button: React.FC<ButtonProps> = ({ variant = 'primary', onClick, children, className = '' }) => {
  const baseStyles = 'px-4 py-2 rounded transition duration-200';
  const variantStyles = variant === 'primary'
    ? 'bg-blue-500 text-white hover:bg-blue-600 active:bg-blue-700'
    : 'bg-yellow-400 text-gray-800 hover:bg-yellow-300 active:bg-yellow-500';

  return (
    <button
      className={`${baseStyles} ${variantStyles} ${className}`}
      onClick={onClick}
      aria-label={`Button: ${children}`}
    >
      {children}
    </button>
  );
};

export default Button;
