// Input.tsx
import React, { useState } from 'react';
/**
 * Standard input field component
 */
interface InputProps {
  variant?: 'email' | 'password' | 'text';
  state?: 'error' | 'focus';
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}
const Input: React.FC<InputProps> = ({
  variant = 'text',
  state = 'normal',
  value,
  onChange,
  placeholder = '',
}) => {
  const [focused, setFocused] = useState(false);
  const handleFocus = () => {
    setFocused(true);
  };
  const handleBlur = () => {
    setFocused(false);
  };
  return (
    <input
      className={
        `px-12px py-8px ${
          state === 'error'
            ? 'border-red-500 focus:ring-red-500'
            : focused
            ? 'focus:ring-2 ring-blue-500'
            : 'focus:ring-2 ring-gray-300'
        } `
      }
      type={variant}
      value={value}
      onChange={(e) => onChange(e.target.value)}
      onFocus={handleFocus}
      onBlur={handleBlur}
      placeholder={placeholder}
    />
  );
};
export default Input;
