// Card.tsx
import React from 'react';
/**
 * Standard card component
 */
interface CardProps {
  variant?: 'default' | 'highlighted';
  children: React.ReactNode;
  className?: string;
}
const Card: React.FC<CardProps> = ({ variant = 'default', children, className }) => {
  const classes = [
    'flex',
    'flex-col',
    'rounded-lg',
    'shadow-lg',
    'p-4',
    {
      'bg-gray-100': variant === 'default',
      'bg-blue-100': variant === 'highlighted',
    },
    className,
  ].filter(Boolean).join(' ');

  return (
    <div className={classes}>
      {children}
    </div>
  );
};
export default Card;
