import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { AiOutlineLoading3Quarters } from 'react-icons/ai';
import { WiError } from 'react-icons/wi';

interface BuildListProps {
  variant?: 'default' | 'compact';
}

const BuildList: React.FC<BuildListProps> = ({ variant = 'default' }) => {
  const [builds, setBuilds] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);

  useEffect(() => {
    const fetchBuilds = async () => {
      try {
        const response = await axios.get('/api/builds');
        setBuilds(response.data);
      } catch (error) {
        setError(true);
      } finally {
        setLoading(false);
      }
    };
    fetchBuilds();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center">
        <AiOutlineLoading3Quarters size={24} className="animate-spin" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex justify-center">
        <WiError size={24} />
        <p>Error loading builds</p>
      </div>
    );
  }

  return (
    <div className={variant === 'compact' ? 'max-w-md' : 'max-w-2xl'}>
      <h2 className="text-lg font-bold">Build List</h2>
      <ul>
        {builds.map((build) => (
          <li key={build.id}>
            <span>{build.name}</span>
            <span>{build.status}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default BuildList;
