import React, { useState } from 'react';

interface InputProps {
  type?: string;
  placeholder?: string;
  onChange: (value: string) => void;
  error?: boolean;
}

const Input: React.FC<InputProps> = ({ type = 'text', placeholder, onChange, error = false }) => {
  const [value, setValue] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setValue(e.target.value);
    onChange(e.target.value);
  };

  const baseStyles = 'border rounded p-2 w-full';
  const errorStyles = 'border-red-500';
  const focusStyles = 'focus:border-blue-500 outline-none';

  return (
    <input
      type={type}
      placeholder={placeholder}
      value={value}
      onChange={handleChange}
      className={`${baseStyles} ${error ? errorStyles : ''} ${focusStyles}`}
      aria-invalid={error}
    />
  );
};

export default Input;
