{
  "in_scope": [
    {
      "feature": "Build List Panel",
      "description": "Displays a list of active and completed builds, including build status, timestamps, and duration.",
      "priority": "P0",
      "acceptance_criteria": [
        "User can view a list of all builds with their status (queued, running, completed, failed)",
        "Each build shows the start and end timestamps, as well as the total duration",
        "User can click on a build to view its detailed information"
      ],
      "boundary_notes": "This panel only displays build metadata, not the actual build artifacts or logs."
    },
    {
      "feature": "Phase Progression Panel",
      "description": "Provides a visual timeline of the various build phases, including their status and details.",
      "priority": "P0",
      "acceptance_criteria": [
        "Panel displays a timeline of the build phases: discovery, design, architecture, frontend, backend, integration, testing, deployment",
