{
  "openapi": "3.0.3",
  "info": {
    "title": "OLYMPUS Builder Dashboard API",
    "version": "1.0.0",
    "description": "API documentation for the OLYMPUS Builder Dashboard"
  },
  "servers": [
    { "url": "/api/v1", "description": "API v1" }
  ],
  "paths": {
    "/builds": {
      "get": {
        "operationId": "listBuilds",
        "summary": "List all builds",
        "tags": ["Builds"],
        "security": [
          { "bearerAuth": [] }
        ],
        "parameters": [
          { "name": "page", "in": "query", "schema": { "type": "integer", "default": 1 } },
          { "name": "pageSize", "in": "query", "schema": { "type": "integer", "default": 20, "maximum": 100 } }
        ],
        "responses": {
          "200": {
            description: "List of builds",
            content: {
              "application/json": {
                schema: {
                  type: "object",
                  properties: {
                    data: {
                      type: "array",
                      items: { $ref: "#/components/schemas/Build" }
                    },
                    meta: { $ref: "#/components/schemas/PaginationMeta" }
                  }
                }
              }
            }
          },
          "401": { "$ref": "#/components/responses/Unauthorized" }
        }
      }
    },
    "/builds/{buildId}": {
      parameters: [
        { "name": "buildId", "in": "path", "required": true, "schema": { "type": "string" } }
      ],
      "get": {
        "operationId": "getBuild",
        "summary": "Get build details",
        "tags": ["Builds"],
        "security": [
          { "bearerAuth": [] }
        ],
        "responses": {
          "200": {
            description: "Build details",
            content: {
              "application/json": {
                schema: {
                  type: "object",
                  properties: {
                    data: { $ref: "#/components/schemas/BuildDetails" }
                  }
                }
              }
            }
          },
