import { getServerSession } from '@/lib/auth';
import { prisma } from '@/lib/prisma';

// SSE for real-time updates
export async function GET(request: NextRequest, context: { params: Promise<{ buildId: string }> }) {
  try {
    const session = await getServerSession();
    if (!session) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { buildId } = await context.params;

    // Here you would implement the SSE connection logic to stream updates
    // For now, we'll return a response indicating the endpoint is ready
    return new Response('Streaming build updates...', {
      headers: { 'Content-Type': 'text/event-stream' },
    });
  } catch (error) {
    console.error('Error establishing SSE connection:', error);
    return NextResponse.json({ error: { code: 'INTERNAL_ERROR', message: 'Failed to establish SSE connection' } }, { status: 500 });
  }
}

