// src/app/api/v1/builds/[buildId]/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { z } from 'zod';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { errorResponse, validationError, notFoundError } from '@/lib/api-utils';

// Zod schema for getting build details
const getBuildDetailsSchema = z.object({
  buildId: z.string().cuid(),
});

type RouteContext = {
  params: Promise<{ buildId: string }>;  
};

// GET /api/v1/builds/[buildId] - Get single build
export async function GET(request: NextRequest, context: RouteContext) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    const { buildId } = await context.params;
    const build = await prisma.build.findFirst({
      where: { id: buildId, deletedAt: null },
    });

    if (!build) {
      return notFoundError('Build');
    }

    return NextResponse.json({ data: build });

  } catch (error) {
    console.error('Error getting build:', error);
    return errorResponse('INTERNAL_ERROR', 'Failed to get build', 500);
  }
}

// DELETE /api/v1/builds/[buildId] - Soft delete build
export async function DELETE(request: NextRequest, context: RouteContext) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    const { buildId } = await context.params;

    const existing = await prisma.build.findFirst({
      where: { id: buildId, deletedAt: null },
    });

    if (!existing) {
      return notFoundError('Build');
    }

    // Soft delete
    await prisma.build.update({
      where: { id: buildId },
      data: { deletedAt: new Date() },
    });

    return new NextResponse(null, { status: 204 });

  } catch (error) {
    console.error('Error deleting build:', error);
    return errorResponse('INTERNAL_ERROR', 'Failed to delete build', 500);
  }
}

// src/app/api/v1/agents/route.ts
import { NextRequest, NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { getServerSession } from '@/lib/auth';
import { errorResponse } from '@/lib/api-utils';

// GET /api/v1/agents - List agents
export async function GET(request: NextRequest) {
  try {
    const session = await getServerSession();
    if (!session) {
      return errorResponse('AUTH_UNAUTHORIZED', 'Authentication required', 401);
    }

    const agents = await prisma.agent.findMany({
      where: { deletedAt: null },
      orderBy: { createdAt: 'asc' },
    });

    return NextResponse.json({ data: agents });

  } catch (error) {
    console.error('Error listing agents:', error);
    return errorResponse('INTERNAL_ERROR', 'Failed to list agents', 500);
  }
}

