{
  "in_scope": [
    {
      "feature": "Build List Panel",
      "description": "Displays a list of active and completed builds with status indicators, timestamps, and duration.",
      "priority": "P0",
      "acceptance_criteria": [
        "User can see a list of all builds with their status (queued, running, completed, failed)",
        "Each build shows timestamp, duration, and status icon",
        "User can click on a build to view its details"
      ],
      "boundary_notes": "Only shows basic build metadata, no ability to deploy, download or override builds in this version."
    },
    {
      "feature": "Phase Progression Panel",
      "description": "Provides a visual timeline of the build process, showing the status of each phase.",
      "priority": "P0",
      "acceptance_criteria": [
        "Panel displays a timeline of build phases (discovery, design, architecture, frontend, backend, integration, testing, deployment)",
        "Each phase shows its status (pending, active, completed, failed), start/end times, and a brief explanation",
        "Timeline updates in real-time as the build progresses"
      ],
      "boundary_notes": "Does not allow user to interact with or modify the phases."
    },
    {
      "feature": "Agent Roster Panel",
      "description": "Lists all 35 agents involved in the build process, showing their current status and tasks.",
      "priority": "P0",
      "acceptance_criteria": [
        "Panel displays a list of all 35 agents organized by build phase",
        "Each agent shows their name, role, current status (idle, working, completed, failed), and current task (if working)",
        "Agent status and task information updates in real-time"
      ],
      "boundary_notes": "No ability to interact with or assign tasks to agents in this version."
    },
    {
      "feature": "Constitutional Status Panel",
