import { PrismaClient, BuildStatus, PhaseStatus, AgentStatus } from '@prisma/client';

const prisma = new PrismaClient();

async function main() {
  // Seed builds
  const builds = await Promise.all([
    prisma.build.create({
      data: {
        id: 'build-1',
        status: BuildStatus.COMPLETED,
        createdAt: new Date('2023-01-01T12:00:00Z'),
        updatedAt: new Date('2023-01-01T12:30:00Z'),
        startTime: new Date('2023-01-01T12:00:00Z'),
        endTime: new Date('2023-01-01T12:30:00Z'),
        duration: 1800
      }
    }),
    prisma.build.create({
      data: {
        id: 'build-2',
        status: BuildStatus.RUNNING,
        createdAt: new Date('2023-01-02T12:00:00Z'),
        updatedAt: new Date('2023-01-02T12:15:00Z'),
        startTime: new Date('2023-01-02T12:00:00Z'),
        endTime: null,
        duration: null
      }
    })
  ]);

  // Seed phases
  await Promise.all([
    prisma.phase.create({
      data: {
        id: 'phase-1',
        buildId: 'build-1',
        name: 'discovery',
        status: PhaseStatus.COMPLETED,
        startTime: new Date('2023-01-01T12:00:00Z'),
        endTime: new Date('2023-01-01T12:10:00Z'),
        explanation: 'Initial discovery phase.'
      }
    }),
    prisma.phase.create({
      data: {
        id: 'phase-2',
        buildId: 'build-1',
        name: 'design',
        status: PhaseStatus.COMPLETED,
        startTime: new Date('2023-01-01T12:10:00Z'),
        endTime: new Date('2023-01-01T12:20:00Z'),
        explanation: 'Design phase where UI/UX is created.'
      }
    })
  ]);

  // Seed agents
  await Promise.all([
    prisma.agent.create({
      data: {
        id: 'agent-1',
        name: 'Agent A',
        role: 'Frontend Developer',
        status: AgentStatus.IDLE,
        currentTask: null,
        phaseId: 'phase-1'
      }
    }),
    prisma.agent.create({
      data: {
        id: 'agent-2',
        name: 'Agent B',
        role: 'Backend Developer',
        status: AgentStatus.WORKING,
        currentTask: 'Implementing API',
        phaseId: 'phase-2'
      }
    })
  ]);
}

main()
