// GET /api/v1/builds - List builds
export async function GET(request: NextRequest) {
  try {
    // 1. Auth check
    const session = await getServerSession();
    if (!session) {
      return NextResponse.json(
        { error: { code: 'AUTH_UNAUTHORIZED', message: 'Authentication required' } },
        { status: 401 }
      );
    }

    // 2. Parse and validate query params
    const searchParams = Object.fromEntries(request.nextUrl.searchParams);
    const query = listBuildsQuerySchema.safeParse(searchParams);

    if (!query.success) {
      return NextResponse.json(
        { error: { code: 'VALIDATION_FAILED', message: 'Invalid query parameters', details: query.error.flatten() } },
        { status: 422 }
      );
    }

    const { page, pageSize } = query.data;

    // 3. Execute query with pagination
    const [builds, total] = await Promise.all([
      prisma.build.findMany({
        skip: (page - 1) * pageSize,
        take: pageSize,
        orderBy: { createdAt: 'desc' },
      }),
      prisma.build.count(),
    ]);

    // 4. Return response envelope
    return NextResponse.json({
      data: builds,
      meta: {
        page,
        pageSize,
        total,
        hasMore: page * pageSize < total,
