{
  "tech_stack": {
    "framework": "Next.js 14 App Router",
    "database": "Supabase PostgreSQL",
    "orm": "Prisma",
    "auth": "Supabase Auth",
    "cache": "Upstash Redis",
    "state": "Zustand",
    "hosting": "Vercel",
    "styling": "Tailwind CSS + shadcn/ui",
    "icons": "Lucide React",
    "animation": "Framer Motion",
    "validation": "Zod"
  },
  "architecture": {
    "pattern": "monolith",
    "reasoning": "The project is an MVP dashboard for power users, which involves a few core features and entities (builds and agents). A monolithic architecture minimizes complexity and allows for rapid iteration. It is suitable given the straightforward requirements and lack of need for separate service boundaries.",
    "scalability": "small",
    "estimated_entities": 6,
    "estimated_api_routes": 6
  },
  "database": {
    "softDeletes": false,
    "auditTimestamps": true,
    "idStrategy": "cuid",
    "cascadeDeletes": false,
    "auditedTables": [
      "builds",
      "agents"
    ],
